import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { getdataAction } from "./Redux/Actions/fetchdataAction";
import LoaderStatus from "./Loader/LoaderStatus";
import { Navigate, RouteObject, useRoutes } from "react-router-dom";
import PublicLayOut from "./Component/PublicComponent/PublicLayOut";
import Registration from "./Component/Login/Registration";
import Login from "./Component/Login/Login";
import TaskShow from "./Component/AddTask/TaskShow";
import Loader from "./Loader/Loader";
import { LoaderAction } from "./Redux/Actions/LoaderAction";


export default function App() {

const dispatch = useDispatch()

  const LoaderState = useSelector((state: any) => state.LoaderReducer);

  const UserData = useSelector((state: any) => state.UserReducer);
   console.log(LoaderState);



  function RequireAuth({ children }: { children: JSX.Element }) {
    dispatch(LoaderAction(true))
    if (sessionStorage.getItem("UserLogin") == "null")  {
      dispatch(LoaderAction(false))
      return <Navigate to="/Login" replace />;
    }

     return children;
  }
  function LoginTrue({ children }: { children: JSX.Element }) {

    if (sessionStorage.getItem("UserLogin") != "null") {
        return <Navigate to="/Task" replace />;   
    }

    return children;
  }
  function CheckLoader({ children }: { children: JSX.Element }) {

    if (LoaderState == true) {
      return <Loader />
    }
    return children;
  }



  let routes: RouteObject[] = [
    {
      path: "/",
      element: <PublicLayOut />,
      children: [
        // { index: true, element: < /> },
        {
          path: "/Registration",
          element: <Registration />
        },

        {
          path: "/Login",
          element: <LoginTrue><Login /></LoginTrue>
        }
      ]
    },
    { path: "*", element: <p>There's nothing here!</p> },
    {
      path: "/Task", element: (<RequireAuth><TaskShow /></RequireAuth>)
    }
  ];
  let element = useRoutes(routes);


  return (
    <div className="App">

<CheckLoader>
      {element}



      </CheckLoader>




    </div>
  );
}
