import React, { useEffect, useState } from 'react'
import { useForm } from 'react-hook-form';
import { useDispatch, useSelector } from 'react-redux';
import { DeleteTask, GetTaskList, TaskStatus, TaskUpdate } from '../../API/Api';
import { GetTaskList1, GetTaskList2 } from '../../Redux/Actions/GetTaskListAction';
import { LoaderAction } from '../../Redux/Actions/LoaderAction';
var uniqid = require('uniqid'); 

function TaskShow() {
  const disptach = useDispatch()
  const [status, setstatus] = useState(false)
  const [IsEdit, setIsEdit] = useState(false)
  const { register, handleSubmit, formState: { errors },setValue } = useForm();
  const TaskList = useSelector((state) => state.GettaskListReducer);
  const onSubmit = data => {  TaskUpdate(disptach, data)  ; setIsEdit(false) }
  if (TaskList == null) {
    disptach(LoaderAction(true))
  }

  useEffect(() => { 
   
      GetTaskList(disptach, sessionStorage.getItem("UserLogin")) 
  }, [])

 


  return (
    <>
       <h1>Pending</h1>
       <form onSubmit={handleSubmit(onSubmit)}>
      {TaskList != null ? TaskList.filter(f=>f.Complete == false).map((x) => {
        return (
          <div className="input-group w-50 my-2" key={uniqid()}>
            <div className="input-group-text" key={uniqid()}>
            <input className='form-check-input' type='radio' onClick={()=>TaskStatus(disptach, x)} key={uniqid()}/>

            </div>
            {IsEdit ==true? <input type="text" {...register("Task"+x.TaskId)} className="form-control" aria-label="Text input with radio button" defaultValue={x.Task}  key={uniqid()}/>:
            <input type="text"  className="form-control" aria-label="Text input with radio button" value={x.Task} readOnly key={uniqid()}/>}

            <button onClick={()=>{DeleteTask(disptach,x.TaskId)}} key={uniqid()} className="btn btn-outline">Delete</button>
          {IsEdit==true?  <button onClick={()=>{setValue("PreData", x)}} key={uniqid()} className="btn" type='submit'>Done</button>:  <button onClick={()=>{setIsEdit(true)}} key={uniqid()} className="btn" >Edit</button>}
          </div>
        )
      })

        : <h1>No Data</h1>}
</form>
    <h1>Complete</h1>

      {TaskList != null ? TaskList.filter(f=>f.Complete == true).map((x) => {
        return (
          <div className="input-group w-50 my-2" key={uniqid()}>
          <div className="input-group-text" key={uniqid()}>
          <input className='form-check-input' type='radio' onClick={()=>TaskStatus(disptach, x)} key={uniqid()}/>

          </div>
          {IsEdit ==true? <input type="text" {...register("Task")} className="form-control" aria-label="Text input with radio button" defaultValue={x.Task}  key={uniqid()}/>:
          <input type="text"  className="text-decoration-line-through form-control" aria-label="Text input with radio button" value={x.Task} readOnly key={uniqid()}/>}

          <button onClick={()=>{DeleteTask(disptach,x.TaskId)}} key={uniqid()} className="btn btn-outline">Delete</button>
        {IsEdit==true?  <button onClick={()=>{setValue("PreData", x)}} key={uniqid()} className="btn" type='submit'>Done</button>:  <button onClick={()=>{setIsEdit(true)}} key={uniqid()} className="btn" >Edit</button>}
        </div>
        )
      })

        : <h1>No Data</h1>}
     
    </>

  )
}

export default TaskShow
