import React, { useEffect, useState } from 'react'
import { useForm } from 'react-hook-form';
import { useDispatch, useSelector } from 'react-redux';
import { DeleteTask, GetTaskList, TaskStatus } from '../../API/Api';
import { GetTaskList1, GetTaskList2 } from '../../Redux/Actions/GetTaskListAction';
import { LoaderAction } from '../../Redux/Actions/LoaderAction';
var uniqid = require('uniqid'); 

function TaskShow() {
  const disptach = useDispatch()
  const [status, setstatus] = useState(false)

  const { register, handleSubmit, formState: { errors } } = useForm();
  const TaskList = useSelector((state: any) => state.GettaskListReducer);
  const onSubmit = data => console.log(data);
  if (TaskList == null) {
    disptach(LoaderAction(true))
  }

  useEffect(() => { 
   
      GetTaskList(disptach, sessionStorage.getItem("UserLogin")) 
  }, [])

 


  return (
    <>
       <h1>Pending</h1>
      {TaskList != null ? TaskList.filter(f=>f.Complete == false).map((x) => {
        return (
          <div className="input-group w-50 my-2" key={uniqid()}>
            <div className="input-group-text" key={uniqid()}>
              <input {...register("radio1")} type="radio"  className="form-check-input mt-0" onChange={()=>{TaskStatus(disptach, x)}} key={uniqid()}/>

            </div>
            <input type="text" className="form-control" aria-label="Text input with radio button" value={x.Task} readOnly key={uniqid()}/>
            <button onClick={()=>{DeleteTask(disptach,x.TaskId)}} key={uniqid()} className="btn">Delete</button>
          </div>
        )
      })

        : <h1>No Data</h1>}

    <h1>Complete</h1>

      {TaskList != null ? TaskList.filter(f=>f.Complete == true).map((x) => {
        return (
          <div className="input-group w-50 my-2" key={uniqid()}>
            <div className="input-group-text" key={uniqid()}>
              <input {...register("radio1")} type="radio"  className="form-check-input mt-0" onChange={()=>TaskStatus(disptach, x)} key={uniqid()}/>

            </div>
            <input type="text" className="form-control" aria-label="Text input with radio button" value={x.Task} key={uniqid()} readOnly/>
            <button onClick={()=>{DeleteTask(disptach,x.TaskId)}} key={uniqid()}>Delete</button>

          </div>
        )
      })

        : <h1>No Data</h1>}
     
    </>

  )
}

export default TaskShow
