import React, { useEffect, useState } from 'react'
import { useForm } from 'react-hook-form';
import { useDispatch, useSelector } from 'react-redux';
import { GetTaskList } from '../../API/Api';
import { GetTaskList1, GetTaskList2 } from '../../Redux/Actions/GetTaskListAction';
import { LoaderAction } from '../../Redux/Actions/LoaderAction';


function TaskShow() {
  const disptach = useDispatch()

  const { register, handleSubmit, formState: { errors } } = useForm();
  const onSubmit = data => console.log(data);
  useEffect(() => {
    if (TaskList == null) {
      disptach(LoaderAction(true))
    }
    return () => {
      GetTaskList(disptach, sessionStorage.getItem("UserLogin"))

    }
  }, [])

  const TaskList = useSelector((state: any) => state.GettaskListReducer);
  console.log(TaskList)

  return (
    <>


      {TaskList != null ? TaskList.map((x) => {
        return (
          <div className="input-group">
            <div className="input-group-text">
              <input {...register("radio1")} type="radio"  className="form-check-input mt-0" />

            </div>
            <input type="text" className="form-control" aria-label="Text input with radio button" value={x.Task}/>
            <button onClick={()=>console.log(x.TaskId)}>Delete</button>

          </div>
        )
      })

        : <h1>No Data</h1>}

    </>

  )
}

export default TaskShow
